webpackJsonp([114],{681:function(n,t){n.exports="import React, { Component } from 'react'\nimport { Transition } from 'key-ui/transition'\nimport { NavBar } from 'key-ui/nav-bar'\nimport { Button, ButtonGroup } from 'key-ui/button'\nimport styles from './Transition.css'\n\nexport default class Demo extends Component {\n    state = {\n        afterKey: 1,\n        direction: 'left',\n    }\n\n    render() {\n        const { afterKey, direction } = this.state\n        const lastKey = direction === 'left' ? afterKey - 1 : afterKey + 1\n\n        return (\n            <div>\n                <NavBar title=\"Transition Demo\" />\n                <Transition\n                    className={styles.root}\n                    afterKey={afterKey}\n                    direction={direction}\n                    before={\n                        <div className={styles[`container${lastKey % 2}`]}>\n                            当前第 {lastKey} 页\n                        </div>\n                    }\n                    after={\n                        <div className={styles[`container${afterKey % 2}`]}>\n                            当前第 {afterKey} 页\n                        </div>\n                    }\n                />\n                <ButtonGroup>\n                    <Button\n                        type=\"secondary\"\n                        onTouchTap={() => {\n                            const next = afterKey - 1\n                            if (next >= 1) {\n                                this.setState({\n                                    afterKey: afterKey - 1,\n                                    direction: 'right',\n                                })\n                            }\n                        }}\n                    >\n                        往前一页\n                    </Button>\n                    <Button\n                        onTouchTap={() => {\n                            this.setState({\n                                afterKey: afterKey + 1,\n                                direction: 'left',\n                            })\n                        }}\n                    >\n                        往后一页\n                    </Button>\n                </ButtonGroup>\n            </div>\n        )\n    }\n}\n"}});