webpackJsonp([120],{675:function(n,e){n.exports="import React, { Component } from 'react'\nimport { NavBar } from 'key-ui/nav-bar'\nimport { List, ListItem } from 'key-ui/list'\nimport { InlineSelect, InlineCascade, Select } from 'key-ui/select'\n\nconst select = [\n    { value: 1, label: '吃饭' },\n    { value: 2, label: '睡觉' },\n    { value: 3, label: '打豆豆' },\n    { value: 4, label: '我就是豆豆' },\n]\n\nconst cascade = [\n    {\n        value: 1,\n        label: '广东',\n        children: [\n            { value: 11, label: '深圳' },\n            { value: 12, label: '广州' },\n        ],\n    },\n    {\n        value: 2,\n        label: '江苏',\n        children: [\n            { value: 21, label: '镇江' },\n        ],\n    },\n]\n\nconst districtData = require('./district.json')\n\nexport default class Demo extends Component {\n    state = {\n        district: null,\n    }\n\n    render() {\n        const { district } = this.state\n\n        return (\n            <div>\n                <NavBar title=\"Select Demo\" />\n                <List header=\"非受控组件\">\n                    <Select\n                        data={select}\n                        defaultValue={3}\n                        renderInline={pair => (\n                            <ListItem\n                                title=\"选择活动\"\n                                content={pair.label}\n                                arrow=\"down\"\n                                alignContent=\"left\"\n                            />\n                        )}\n                    />\n                    <Select\n                        data={cascade}\n                        defaultValue={[2, 21]}\n                        cascade\n                        renderInline={pairs => (\n                            <ListItem\n                                title=\"选择地区\"\n                                content={pairs.map(e => e.label)}\n                                arrow=\"down\"\n                                alignContent=\"left\"\n                            />\n                        )}\n                    />\n                </List>\n                <List header=\"受控组件\">\n                    <Select\n                        data={districtData}\n                        dataKeyMap={{ value: 'code', label: 'name', children: 'subNames' }}\n                        cascade\n                        value={district}\n                        onChange={(values) => {\n                            this.setState({ district: values })\n                        }}\n                        renderInline={pairs => (\n                            <ListItem\n                                title=\"选择地区\"\n                                content={pairs.map(e => e.name)}\n                                arrow=\"down\"\n                                alignContent=\"left\"\n                            />\n                        )}\n                    />\n                </List>\n                <List header=\"InlineSelect\">\n                    <InlineSelect data={select} />\n                </List>\n                <List header=\"InlineCascade\">\n                    <InlineCascade data={cascade} />\n                </List>\n            </div>\n        )\n    }\n}\n"}});