webpackJsonp([127],{668:function(n,t){n.exports="import React, { Component } from 'react'\nimport { NavBar } from 'key-ui/nav-bar'\nimport { Mask } from 'key-ui/mask'\nimport { Button } from 'key-ui/button'\nimport styles from './Mask.css'\n\nexport default class Demo extends Component {\n    state = {\n        mask: false,\n    }\n\n    render() {\n        return (\n            <div>\n                <NavBar title=\"Mask Demo\" />\n                <Button\n                    type=\"secondary\"\n                    onTouchTap={() => this.setState({ mask: true })}\n                >\n                    显示遮罩\n                </Button>\n                {this.state.mask && (\n                    <Mask\n                        show={this.state.mask}\n                        onTouchTap={() => this.setState({ mask: false })}\n                    >\n                        <div className={styles.root}>\n                            点击遮罩关闭\n                        </div>\n                    </Mask>\n                )}\n            </div>\n        )\n    }\n}\n"}});