webpackJsonp([129],{666:function(n,e){n.exports='import React, { Component } from \'react\'\nimport { NavBar } from \'key-ui/nav-bar\'\nimport { List } from \'key-ui/list\'\nimport { InputItem } from \'key-ui/input-item\'\nimport { Icon } from \'key-ui/icon\'\nimport styles from \'./InputItem.css\'\n\nexport default class Demo extends Component {\n    state = {\n        multiLineValue: \'\',\n        sendCountdown: 0,\n        iconValue: \'\',\n    }\n\n    handleTouchSend = () => {\n        if (this.state.sendCountdown === 0) {\n            this.setState({ sendCountdown: 5 })\n            const timer = setInterval(() => {\n                let n = this.state.sendCountdown - 1\n                if (n < 0) n = 0\n                this.setState({ sendCountdown: n })\n                if (n === 0) clearInterval(timer)\n            }, 1000)\n        }\n    }\n\n    render() {\n        const { multiLineValue, sendCountdown, iconValue } = this.state\n\n        return (\n            <div>\n                <NavBar title="InputItem Demo" />\n                <List header="标题单行">\n                    <InputItem title="文字标题" placeholder="带清空按钮" />\n                    <InputItem\n                        title="文字标题"\n                        placeholder="不带清空按钮"\n                        onChange={e => console.log(\'onChange\', e.target.value)}\n                    />\n                    <InputItem\n                        title="文字标题"\n                        defaultValue="错误提示"\n                        inputClass={styles.error}\n                    />\n                </List>\n                <List header="标题多行">\n                    <InputItem\n                        title="文字标题两行显示"\n                        placeholder="受控组件"\n                        value={multiLineValue}\n                        onChange={(e) => {\n                            this.setState({ multiLineValue: e.target.value })\n                        }}\n                    />\n                </List>\n                <List header="通栏输入">\n                    <InputItem placeholder="请输入账号" />\n                    <InputItem placeholder="请输入密码" type="password" />\n                </List>\n                <List header="自定义输入">\n                    <InputItem\n                        title="验证码"\n                        placeholder="请输入验证码"\n                        type="tel"\n                        arrow={\n                            <div\n                                className={styles.send}\n                                onTouchTap={this.handleTouchSend}\n                            >\n                                发送\n                                {sendCountdown > 0 ? `(${sendCountdown})` : \'\'}\n                            </div>\n                        }\n                    />\n                    <InputItem\n                        title="银行卡号"\n                        magnifyingPosition="above"\n                        defaultValue="12345678"\n                        maxLength="20"\n                    />\n                    <InputItem\n                        title="定制箭头"\n                        value={iconValue}\n                        onChange={(e) => {\n                            this.setState({ iconValue: e.target.value })\n                        }}\n                        arrow={[\n                            <Icon\n                                key="1"\n                                type="camera"\n                                className={styles.camera}\n                                onTouchTap={(e) => {\n                                    e.preventDefault()\n                                    e.stopPropagation()\n                                    this.setState({ iconValue: \'你点击了相机\' })\n                                }}\n                            />,\n                            <Icon\n                                key="2"\n                                type="location"\n                                onTouchTap={(e) => {\n                                    e.preventDefault()\n                                    e.stopPropagation()\n                                    this.setState({ iconValue: \'你点击了定位\' })\n                                }}\n                            />,\n                        ]}\n                    />\n                </List>\n            </div>\n        )\n    }\n}\n'}});