webpackJsonp([132],{664:function(t,n){t.exports="import React, { Component } from 'react'\nimport { NavBar } from 'key-ui/nav-bar'\nimport { List, ListItem } from 'key-ui/list'\nimport { FlowSelector } from 'key-ui/flow-selector'\n\nconst districtData = require('./district.json')\n\nexport default class Demo extends Component {\n    state = {\n        show: false,\n        district: null,\n    }\n\n    render() {\n        const { district, show } = this.state\n\n        return (\n            <div>\n                <NavBar title=\"FlowSelector Demo\" />\n                <List header=\"FlowSelector\">\n                    <ListItem\n                        title=\"选择地区\"\n                        content={district ? district.map(e => e.name) : '请选择'}\n                        onTouchTap={() => this.setState({ show: true })}\n                        arrow=\"down\"\n                        alignContent=\"left\"\n                    />\n                </List>\n                <FlowSelector\n                    show={show}\n                    data={districtData}\n                    dataKeyMap={{\n                        value: 'code',\n                        label: 'name',\n                        children: 'subNames',\n                    }}\n                    value={district ? district.map(e => e.code) : null}\n                    onChange={(values, pairs, event) => {\n                        this.setState({ show: false, district: pairs })\n                    }}\n                    onCancel={() => this.setState({ show: false })}\n                    headerLabel={['请选择省', '请选择市', '请选择区']}\n                />\n            </div>\n        )\n    }\n}\n"}});