webpackJsonp([134],{700:function(n,e){n.exports="import React, { Component } from 'react'\nimport { SearchBar } from 'key-ui/search-bar'\nimport { DefaultPage } from 'key-ui/default-page'\nimport { Icon } from 'key-ui/icon'\n\nexport default class Demo extends Component {\n    state = {\n        input: '',\n        isSearching: false,\n    }\n\n    onSearchChange = (value) => {\n        this.setState({ input: value, isSearching: false })\n    }\n\n    onSearchCancel = () => {\n        this.setState({\n            input: '',\n            isSearching: false,\n        })\n    }\n\n    onSearchConfirm = () => {\n        this.setState({\n            isSearching: true,\n        })\n    }\n\n    render() {\n        return (\n            <div>\n                <SearchBar\n                    input={this.state.input}\n                    placeholder=\"搜索\"\n                    onChange={this.onSearchChange}\n                    onCancel={this.onSearchCancel}\n                    onConfirm={this.onSearchConfirm}\n                    onEmptyConfirm={e => alert('输入为空')}\n                />\n                {this.state.isSearching && (\n                    <DefaultPage\n                        icon={<Icon type=\"empty-page\" />}\n                        title=\"暂无数据\"\n                        content={`没有找到：${this.state.input}`}\n                    />\n                )}\n            </div>\n        )\n    }\n}\n"}});