webpackJsonp([145],{650:function(n,e){n.exports="import React, { Component } from 'react'\nimport { ActionSheet, ActionRadioGroup } from 'key-ui/action-sheet'\nimport { NavBar } from 'key-ui/nav-bar'\nimport { Button } from 'key-ui/button'\nimport { Icon } from 'key-ui/icon'\nimport styles from './ActionSheet.css'\n\nexport default class Demo extends Component {\n    state = {\n        as1: false,\n        as2: false,\n        as3: false,\n        value: null,\n    }\n\n    render() {\n        return (\n            <div>\n                <NavBar title=\"ActionSheet Demo\" />\n                <Button type=\"secondary\" onTouchTap={() => this.setState({ as1: true })}>\n                    默认形式\n                </Button>\n                <ActionSheet\n                    show={this.state.as1}\n                    data={['选项一', '选项二', '选项三']}\n                    onChange={(index, e) => { this.setState({ as1: false }) }}\n                    onCancel={() => this.setState({ as1: false })}\n                />\n\n                <Button type=\"secondary\" onTouchTap={() => this.setState({ as3: true })}>\n                    单选形式\n                </Button>\n                <ActionRadioGroup\n                    show={this.state.as3}\n                    data={[\n                        { value: 0, label: '选项一' },\n                        { value: 1, label: '选项二' },\n                        { value: 2, label: '选项三' },\n                        { value: 3, label: '禁用选项', disabled: true },\n                    ]}\n                    value={this.state.value}\n                    onChange={(value, event) => {\n                        this.setState({ value, as3: false })\n                    }}\n                    onCancel={() => this.setState({ as3: false })}\n                />\n\n                <Button type=\"secondary\" onTouchTap={() => this.setState({ as2: true })}>\n                    自定义渲染形式\n                </Button>\n                <ActionSheet\n                    show={this.state.as2}\n                    data={['选项一', '选项二', '选项三', '选项四', '选项五', '选项六']}\n                    onChange={(index, e) => this.setState({ as2: false })}\n                    onCancel={() => this.setState({ as2: false })}\n                    renderSheet={(data, onChange) => (\n                        <div className={styles.container}>\n                            <div className={styles.title}>\n                                本网页由 guanjia.pingan.com 提供\n                            </div>\n                            {data.map((e, i) => (\n                                <div\n                                    key={e}\n                                    onTouchTap={(event) => {\n                                        if (onChange) onChange(i, event)\n                                    }}\n                                    className={styles.option}\n                                >\n                                    <Icon type=\"camera\" className={styles.icon} />\n                                    {e}\n                                </div>\n                            ))}\n                        </div>\n                    )}\n                    cancelLabel=\"Cancel\"\n                />\n            </div>\n        )\n    }\n}\n"}});