webpackJsonp([148],{686:function(n,e){n.exports="import React, { Component, PropTypes } from 'react'\nimport { FileSelector } from 'key-ui/file-selector'\nimport { NavBar } from 'key-ui/nav-bar'\n\nconst Tips = props => (\n    <div style={{ padding: '10px', color: '#999' }}>{props.children}</div>\n)\n\nTips.propTypes = {\n    children: PropTypes.node,\n}\n\nconst Wrapper = props => (\n    <div\n        style={{\n            padding: '10px',\n            background: 'white',\n            marginBottom: '10px',\n        }}\n    >\n        {props.children}\n    </div>\n)\n\nWrapper.propTypes = {\n    children: PropTypes.node,\n}\n\nexport default class Demo extends Component {\n    render() {\n        return (\n            <div>\n                <NavBar title=\"FileSelector Demo\" />\n                <Tips>默认</Tips>\n                <Wrapper>\n                    <FileSelector />\n                </Wrapper>\n\n                <Tips>max & multiple</Tips>\n                <Wrapper>\n                    <FileSelector multiple={false} max={3} />\n                </Wrapper>\n\n                <Tips>fixed</Tips>\n                <Wrapper>\n                    <FileSelector fixed max={3} />\n                </Wrapper>\n\n                <Tips>renderInput</Tips>\n                <Wrapper>\n                    <FileSelector\n                        renderInput={file =>\n                            (file ? <div>{file.name}</div> : <div>+</div>)}\n                    />\n                </Wrapper>\n\n                <Tips>get files</Tips>\n                <Wrapper>\n                    <FileSelector\n                        ref={(selector) => {\n                            this.selector = selector\n                        }}\n                    />\n                    <div onTouchTap={() => console.log(this.selector.files)}>\n                        已选择文件\n                    </div>\n                </Wrapper>\n            </div>\n        )\n    }\n}\n"}});