webpackJsonp([153],{681:function(n,e){n.exports="import React, { Component } from 'react'\nimport { NavBar } from 'key-ui/nav-bar'\nimport { ContextMenu, ContextRadioGroup } from 'key-ui/context-menu'\nimport { Button } from 'key-ui/button'\nimport { Icon } from 'key-ui/icon'\nimport styles from './ContextMenu.css'\n\nexport default class Demo extends Component {\n    state = {\n        modal2: false,\n        modal2X: 0,\n        modal2Y: 0,\n\n        modal1: false,\n        modal1X: 0,\n        modal1Y: 0,\n        options: [\n            { value: 1, name: '吃饭' },\n            { value: 2, name: '睡觉' },\n            { value: 3, name: '打豆豆' },\n            { value: 4, name: '我就是豆豆' },\n        ],\n    }\n\n    render() {\n        const selected = this.state.options.find(item => item.selected === true)\n        return (\n            <div>\n                <NavBar title=\"ContextMenu Demo\" />\n                <Button\n                    type=\"secondary\"\n                    onTouchTap={(e) => {\n                        this.setState({\n                            modal2: true,\n                            modal2X: e.nativeEvent.changedTouches[0].pageX,\n                            modal2Y:\n                            e.nativeEvent.changedTouches[0].pageY -\n                            window.scrollY,\n                        })\n                    }}\n                >\n                    ContextMenu\n                </Button>\n                <ContextMenu\n                    show={this.state.modal2}\n                    x={this.state.modal2X}\n                    y={this.state.modal2Y}\n                    onClose={(e) => {\n                        this.setState({\n                            modal2: false,\n                        })\n                    }}\n                >\n                    <div className={styles.example}><Icon type=\"warn\" />&nbsp;children在此</div>\n                </ContextMenu>\n                <Button\n                    type=\"secondary\"\n                    onTouchTap={(e) => {\n                        this.setState({\n                            modal1: true,\n                            modal1X: e.nativeEvent.changedTouches[0].pageX,\n                            modal1Y:\n                            e.nativeEvent.changedTouches[0].pageY -\n                            window.scrollY,\n                        })\n                    }}\n                >\n                    {selected ? selected.name : 'ContextRadioGroup'}\n                </Button>\n                <ContextRadioGroup\n                    show={this.state.modal1}\n                    x={this.state.modal1X}\n                    y={this.state.modal1Y}\n                    onClose={(e) => {\n                        this.setState({\n                            modal1: false,\n                        })\n                    }}\n                    options={this.state.options}\n                    onSelect={(value) => {\n                        this.setState({\n                            modal1: false,\n                            // eslint-disable-next-line\n                            options: this.state.options.map((option) => option.value === value ? { ...option, selected: true } : { ...option, selected: false } )})\n                    }}\n                />\n            </div>\n        )\n    }\n}\n\n"}});